let value = 0;
const counterReducer = (state = 0, action) => {
  switch (action.type) {
    case 'INCREMENT':
      state = state + 1;
      value = state;
      return state;
    case 'DECREMENT':
      state = state - 1;
      value = state;
      return state;
    default:
      value = state;
      return state;
  }
};

export default counterReducer;
export { value };
