const viewReducer = (state = 0, action) => {
  let myItem = localStorage.getItem('myData');
  switch (action.type) {
    case 'SHOW':
      if (state > 0) {
        console.log(myItem);
        let count=myItem * myItem * 3.14
        return count;
      } else {
        console.log(myItem);
        return 'Cannot calculate';
      }
    default:
      return state;
  }
};

export default viewReducer;
