const viewReducer = (state = value, action) => {
  switch (action.type) {
    case 'SHOW':
      if (state > 0) {
        return state * state * 3.14;
      } else {
        return 'Cannot calculate';
      }
    default:
      return state;
  }
};

export default viewReducer;
