import { useEffect } from "react";

const viewReducer = (state = 0, action) => {
  useEffect(()=>{
    setStorage
  })
  let myItem = localStorage.getItem('myData');
  switch (action.type) {
    case 'SHOW':
      if (state > 0) {
        return myItem * myItem * 3.14;
      } else {
        return 'Cannot calculate';
      }
    default:
      return state;
  }
};

export default viewReducer;
